<!DOCTYPE html><html><meta charset=utf-8><meta content="Ulisses João de Oliveira"name=author><meta content="width=device-width,initial-scale=1"name=viewport><title>Repeated Words Counter</title><body style=text-align:center><h1>Repeated Words Counter</h1><div style=margin:25px><input id=minimum_word_length onchange=update_length(this.value) placeholder="Minimum word length"style=padding:10px;margin-right:5px> <input id=minimum_word_repititions onchange=update_repititions(this.value) placeholder="Minimum word repititions"style=padding:10px;margin-left:5px></div><div style=margin:25px><textarea cols=50 id=text placeholder="Paste or write your text here!"rows=12 style=padding:10px></textarea></div><div style=margin:25px><h2>Repeated words:</h2><div style=padding:20px id=words></div></div><h4 style="padding-top: 200px;">All rights reserved to <a href="https://ulissesjdeo.herokuapp.com">Ulisses João de Oliveira</a>.</h4></body><script>let words=document.getElementById('words');let word_separator=String(' ');let listed_words=Array();let result=Array();let txt=String();let element=null;let tmp=null;let min_word_size=3;let min_repititions_number=2;function update_length(value){min_word_size=value;run()}function update_repititions(value){min_repititions_number=value;run()}function process(word){if(word.length>=min_word_size){if(listed_words.includes(word)){result.forEach(function(i){if(i[0]==word){i[1]+=1}})}else{result.push([word,1])}listed_words.push(word)}}function run(){words.innerHTML='';result=Array();listed_words=Array();txt=document.getElementById('text').value;txt=txt.replace(/[^\w\s]/gi,'');txt=txt.split(word_separator);txt.forEach(process);result.forEach(function(obj){if(obj[1]>=min_repititions_number){element=document.createElement('p');element.style.fontSize='16px';tmp=document.createElement('b');tmp.innerHTML=obj[0];tmp.style.fontSize='20px';element.append(tmp);element.append(' appeared ');tmp=document.createElement('b');tmp.innerHTML=`${obj[1]} times`;tmp.style.fontSize='20px';element.append(tmp);words.append(element)}})}let repeat=setInterval(run,500);</script></html>